import React, { Component } from "react";
import PlayerDataService from "../../services/player.service";
import { Link } from "react-router-dom";

export default class PlayersList extends Component {
  constructor(props) {
    super(props);
    this.retrievePlayers = this.retrievePlayers.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActivePlayer = this.setActivePlayer.bind(this);
    this.removeAllPlayers = this.removeAllPlayers.bind(this);

    this.state = {
      players: [],
      currentPlayerl: null,
      currentIndex: -1
    };
  }

  componentDidMount() {
    this.retrievePlayers();
  }

  onChangeSearchPlayerName(e) {
    const searchPlayerName = e.target.value;

    this.setState({
      searchPlayerName: searchPlayerName
    });
  }

  retrievePlayers() {
    PlayerDataService.getAll()
      .then(response => {
        this.setState({
          players: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrievePlayers();
    this.setState({
      currentPlayer: null,
      currentIndex: -1
    });
  }

  setActivePlayer(player, index) {
    this.setState({
      currentPlayer: player,
      currentIndex: index
    });
  }

  removeAllPlayers() {
    PlayerDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchPlayerName() {
    PlayerDataService.findByPlayerName(this.state.searchPlayerName)
      .then(response => {
        this.setState({
          players: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchPlayerName, players, currentPlayer, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by PlayerName"
              value={searchPlayerName}
              onChange={this.onChangeSearchPlayerName}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchPlayerName}
              >
                Search by Player
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Players List</h4>

          <ul className="list-group">
            {players &&
              players.map((player, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActivePlayer(player, index)}
                  key={index}
                >
                  {player.player_name}
                </li>
              ))}
          </ul>
        </div>
        <div className="col-md-6">
          {currentPlayer ? (
            <div>
              <h4>Player</h4>
              <div>
                <label>
                  <strong>Player Name:</strong>
                </label>{" "}
                {currentPlayer.player_name}
              </div>

              <div>
                <label>
                  <strong>Player Name:</strong>
                </label>{" "}
                {currentPlayer.player_id}
              </div>

              <div>
                <label>
                  <strong>Player Class:</strong>
                </label>{" "}
                {currentPlayer.player_class}
              </div>

              <div>
                <label>
                  <strong>Level:</strong>
                </label>{" "}
                {currentPlayer.level}
              </div>

              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentPlayer.status}
              </div>

              <div>
                <label>
                  <strong>Stat Array:</strong>
                </label>{" "}
                {currentPlayer.stat_array}
              </div>

              <div>
                <label>
                  <strong>Location:</strong>
                </label>{" "}
                {currentPlayer.location_id}
              </div>

              <Link
                to={"/players/" + currentPlayer.player_id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Player...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
