import React, { Component } from "react";
import PlayerDataService from "../../services/player.service";

export default class Player extends Component {
  constructor(props) {
    super(props);
    this.onChangePlayerName = this.onChangePlayerName.bind(this);
    this.onChangePlayerRank = this.onChangePlayerRank.bind(this);
    this.onChangeDiscordId = this.onChangeDiscordId.bind(this);
    this.onChangeUserId = this.onChangeUserId.bind(this);
    this.getPlayer = this.getPlayer.bind(this);
    this.updatePlayer = this.updatePlayer.bind(this);
    this.deletePlayer = this.deletePlayer.bind(this);

    this.state = {
      currentPlayer: {
      player_id: null,
      player_rank: "",
      player_name: "",
      discord_id: "",
      user_id: null,

      submitted: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getPlayer(this.props.match.params.player_id);
  }

  onChangePlayerName(e) {
    const player_name = e.target.value;

    this.setState(function(prevState) {
      return {
        currentPlayer: {
          ...prevState.currentPlayer,
          player_name: player_name
        }
      };
    });
  }

  onChangePlayerRank(e) {
    const player_rank = e.target.value;

    this.setState(function(prevState) {
      return {
        currentPlayer: {
          ...prevState.currentPlayer,
          player_rank: player_rank
        }
      };
    });
  }

  onChangeDiscordId(e) {
    const discord_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentPlayer: {
          ...prevState.currentPlayer,
          discord_id: discord_id
        }
      };
    });
  }

  onChangeStatus(e) {
    const status = e.target.value;

    this.setState(function(prevState) {
      return {
        currentPlayer: {
          ...prevState.currentPlayer,
          status: status
        }
      };
    });
  }

  getPlayer(player_id) {
    PlayerDataService.get(player_id)
      .then(response => {
        this.setState({
          currentPlayer: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updatePlayer() {
    PlayerDataService.update(
      this.state.currentPlayer.player_id,
      this.state.currentPlayer
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The Player was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deletePlayer() {    
    PlayerDataService.delete(this.state.currentPlayer.player_id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/Players')
      })
      .catch(e => {
        console.log(e);

      });
  }

  render() {
    const { currentPlayer } = this.state;

    return (
      <div>
        {currentPlayer ? (
          <div className="edit-form">
            <h4>Player</h4>
            <form>
              <div className="form-group">
                <label htmlFor="player_name">Player Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="player_name"
                  value={currentPlayer.player_name}
                  onChange={this.onChangePlayerName}
                />
              </div>

              <div className="form-group">
                <label htmlFor="player_class">Player Class</label>
                <input
                  type="text"
                  className="form-control"
                  id="player_class"
                  value={currentPlayer.player_class}
                  onChange={this.onChangePlayerClass}
                />
              </div>

              <div className="form-group">
                <label htmlFor="level">Level</label>
                <input
                  type="text"
                  className="form-control"
                  id="level"
                  value={currentPlayer.level}
                  onChange={this.onChangeLevel}
                />
              </div>

              <div className="form-group">
                <label htmlFor="status">Status</label>
                <input
                  type="text"
                  className="form-control"
                  id="status"
                  value={currentPlayer.status}
                  onChange={this.onChangeStatus}
                />
              </div>

              <div className="form-group">
                <label htmlFor="stat_array">Stat Array</label>
                <input
                  type="text"
                  className="form-control"
                  id="stat_array"
                  value={currentPlayer.stat_array}
                  onChange={this.onChangeStatArray}
                />
              </div>

              <div className="form-group">
                <label htmlFor="location_id">Location</label>
                <input
                  type="text"
                  className="form-control"
                  id="location_id"
                  value={currentPlayer.location_id}
                  onChange={this.onChangeLocationId}
                />
              </div>

              <div className="form-group">
                <label htmlFor="palyer_id">Player</label>
                <input
                  type="text"
                  className="form-control"
                  id="player_id"
                  value={currentPlayer.player_id}
                  onChange={this.onChangePlayerId}
                />
              </div>

            </form>

            <button
              className="badge badge-danger mr-2"
              onClick={this.deletePlayer}
            >
              Delete
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updatePlayer}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Player...</p>
          </div>
        )}
      </div>
    );
  }
}
