//import { response } from "express";
import React, { Component } from "react";
import AuthService from "../services/auth.service";
import CharacterService from "../services/character.service";
import Carousel from 'react-bootstrap/Carousel'

import { Link } from "react-router-dom";

export default class Profile extends Component {

  constructor(props) {
    super(props);
    
    this.setActiveCharacter = this.setActiveCharacter.bind(this);

    this.state = {
      currentUser: AuthService.getCurrentUser(),
      currentPlayer: "",
      characters: [],
      currentCharacterl: null,
      currentIndex: -1,
    };
    
  }

  setActiveCharacter(character, index) {
    this.setState({
      currentCharacter: character,
      currentIndex: index
    });
  }

  componentDidMount() {

    AuthService.getCurrentPlayer().then(
      response => {
        this.setState({
          currentPlayer: response.data
        }, () =>

        CharacterService.findCharactersByPlayer(this.state.currentPlayer.player_id).then(
          async (data) => {
            console.log(data);
            this.setState({
              characters: data.data
            })
          })
        )  
    }
    )
  }

  render() {
    const { currentUser, currentPlayer, characters, currentCharacter, currentIndex } = this.state;

    return (
      <div className="container">
        <header className="jumbotron">
          <div classname="row"> 
          <div className="col-md-5">
          <h3>
            <strong>{currentUser.username}</strong>'s Profile
          </h3>
          </div>

          <div className="col-md-7">
          <p>
          <strong>Player Name: </strong>{" "}
          {currentPlayer.player_name}
        </p>

        <p>
          <strong>Rank: </strong>{" "}
          {currentPlayer.player_rank}
        </p>

        <p>
          <strong>Discord ID: </strong>{" "}
          {currentPlayer.discord_id}
        </p>
          </div>
          </div>
        </header>
        
          <div class="row">
          <div class="characterList">
        <ul className="list-group">

          <div class="push4">
          <h4>Characters List</h4>
          </div>
            {characters &&
              characters.map((character, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveCharacter(character, index)}
                  key={index}
                >
                  {character.character_name}
                </li>
              ))}
          </ul>   

        <Link to={"/addCharacter"} className="nav-link">
            <button className="btn btn-warning">
            Add Character
            </button>
        </Link>
      </div>

      <div classname="col-md-8">
        <div class="characterTabUp">
      <div class="characterTab">
      <div className="col-md-6">
          {currentCharacter ? (
            <div>
             
              <div>
                <label>
                  <strong>Character Name:</strong>
                </label>{" "}
                {currentCharacter.character_name}
              </div>

              <div>
                <label>
                  <strong>Player Name:</strong>
                </label>{" "}
                {currentCharacter.player_id}
              </div>

              <div>
                <label>
                  <strong>Character Class:</strong>
                </label>{" "}
                {currentCharacter.character_class}
              </div>
              </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Character...</p>
            </div>
          )}
        
      </div>

      <div className="col-md-4">
      {currentCharacter ? (
          <div>

            
            <div>
              <label>
                <strong>Status:</strong>
              </label>{" "}
              {currentCharacter.status}
            </div>

            <div>
              <label>
                <strong>Stat Array:</strong>
              </label>{" "}
              {currentCharacter.stat_array}
            </div>

            <div>
              <label>
                <strong>Location:</strong>
              </label>{" "}
              {currentCharacter.location_id}
            </div>
            
          </div>
        ) : (
          <div>
            <br />
            <p></p>
          </div>
        )}
      </div>

      <div className="col-md-4">
      {currentCharacter ? (
          <div class="characterButtonRight2">

            <Link
              to={"/characters/" + currentCharacter.character_id}
              className="btn btn-warning"
            >
              Edit
            </Link>
            
          </div>
        ) : (
          <div>
            <br />
            <p></p>
          </div>
        )}
      </div>
      </div>
    </div>
    </div>
    </div>
    </div>
    );
  }
}
