import React, { Component } from "react";
import CharacterDataService from "../../services/character.service";

export default class Character extends Component {
  constructor(props) {
    super(props);
    this.onChangeCharacterName = this.onChangeCharacterName.bind(this);
    this.onChangeCharacterClass = this.onChangeCharacterClass.bind(this);
    this.onChangeLevel = this.onChangeLevel.bind(this);
    this.onChangerStatus = this.onChangeStatus.bind(this);
    this.onChangeStatArray = this.onChangeStatArray.bind(this);
    this.onChangeLocationId = this.onChangeLocationId.bind(this);
    this.onChangeUserId = this.onChangeUserId.bind(this);
    this.onChangeApproved = this.onChangeApproved.bind(this);
    this.getCharacter = this.getCharacter.bind(this);
    this.updateCharacter = this.updateCharacter.bind(this);
    this.deleteCharacter = this.deleteCharacter.bind(this);

    this.state = {
      currentCharacter: {
      character_id: null,
      character_name: "",
      character_class: "",
      level: null,
      status: "",
      stat_array: "",
      user_id: null,
      location_id: null,
      approved: null,

      submitted: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getCharacter(this.props.match.params.character_id);
  }

  onChangeCharacterName(e) {
    const character_name = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          character_name: character_name
        }
      };
    });
  }

  onChangeCharacterClass(e) {
    const character_class = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          character_class: character_class
        }
      };
    });
  }

  onChangeLevel(e) {
    const level = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          level: level
        }
      };
    });
  }

  onChangeStatus(e) {
    const status = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          status: status
        }
      };
    });
  }

  onChangeStatArray(e) {
    const stat_array = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          stat_array: stat_array
        }
      };
    });
  }

  onChangeLocationId(e) {
    const location_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          location_id: location_id
        }
      };
    });
  }

  onChangeUserId(e) {
    const user_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          user_id: user_id
        }
      };
    });
  }

  onChangeApproved(e) {
    const approved = e.target.value;

    this.setState(function(prevState) {
      return {
        currentCharacter: {
          ...prevState.currentCharacter,
          approved: approved
        }
      };
    });
  }

  getCharacter(character_id) {
    CharacterDataService.get(character_id)
      .then(response => {
        this.setState({
          currentCharacter: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateCharacter() {
    CharacterDataService.update(
      this.state.currentCharacter.character_id,
      this.state.currentCharacter
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The Character was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteCharacter() {    
    CharacterDataService.delete(this.state.currentCharacter.character_id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/Characters')
      })
      .catch(e => {
        console.log(e);

      });
  }

  render() {
    const { currentCharacter } = this.state;

    return (
      <div>
        {currentCharacter ? (
          <div className="edit-form">
            <h4>Character</h4>
            <form>
            <div className="form-group">
              <label htmlFor="character_name">Name</label>
              <input
                type="text"
                className="form-control"
                id="character_name"
                required
                value={this.state.character_name}
                onChange={this.onChangeCharacterName}
                name="character_name"
                placeholder="Your character's name"
              />
            </div>

            <div className="form-group">
              <label htmlFor="character_class">Class</label>
              <input
                type="text"
                className="form-control"
                id="character_class"
                required
                value={this.state.character_class}
                onChange={this.onChangeCharacterClass}
                name="character_class"
                placeholder="Your character's class"
              />
            </div>

            <div className="form-group">
              <label htmlFor="level">Level</label>
              <input
                type="number"
                className="form-control"
                id="level"
                required
                value={this.state.level}
                onChange={this.onChangeLevel}
                name="level"
                min="1"
                max="20"
                placeholder="Your character's level"
              />
            </div>

            <div className="form-group">
              <label htmlFor="status">Status</label>
              <select className="form-control" id="status" required value={this.state.status}
                onChange={this.onChangeStatus}
                name="status"
                placeholder="Your character's current status">
              <option value= "Alive">Alive</option>
              <option value= "Retired">Retired</option>
              <option value= "Deceased">Deceased</option>
            </select>
            </div>

            <div className="form-group">
              <label htmlFor="stat_array">Stat Array</label>
              <input
                type="text"
                className="form-control"
                id="stat_array"
                required
                value={this.state.stat_array}
                onChange={this.onChangeStatArray}
                name="stat_array"
                placeholder="Ex. 11, 11, 11, 11, 11, 11"
              />
            </div>

            <div className="form-group">
              <label htmlFor="location">Location</label>
              <select className="form-control" id="location" required value={this.state.location}
                onChange={this.onChangeLocationId}
                name="location"
                placeholder="Your character's current status">
              {locations.map((location, index) => (
                <option value={location.location_id} key={index}>{location.location_name}</option>
              ))}
            </select>
            </div>
                
            {checkForAdmin && (
            <div className="form-group">
              <label htmlFor="user_id">User</label>
              <input
                type="text"
                className="form-control"
                id="user_id"
                required
                value={this.state.user_id}
                onChange={this.onChangeUserId}
                name="user_id"
                placeholder="[ADMIN FIELD] User"
              />
            </div>
            )}

            </form>

            <button
              className="badge badge-danger mr-2"
              onClick={this.deleteCharacter}
            >
              Delete
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updateCharacter}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Character...</p>
          </div>
        )}
      </div>
    );
  }
}
