import React, { Component } from "react";
import AuthService from "../services/auth.service";
import PlayerService from "../services/player.service";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentUser: AuthService.getCurrentUser(),
      currentPlayer: PlayerService.findPlayerByUser(AuthService.getCurrentUser().user_id)
    };
  }

  render() {
    const { currentUser, currentPlayer } = this.state;

    return (
      <div className="container">
        <header className="jumbotron">
          <h3>
            <strong>{currentUser.username}</strong>'s Profile
          </h3>
        </header>
        <p>
          <strong>Player Name: </strong>{" "}
          {this.state.currentPlayer.player_name}
        </p>
        <strong>Authorities:</strong>
        <ul>
          {currentUser.roles &&
            currentUser.roles.map((role, index) => <li key={index}>{role}</li>)}
        </ul>
      </div>
    );
  }
}
