//import { response } from "express";
import React, { Component } from "react";
import AuthService from "../services/auth.service";
import CharacterService from "../services/character.service";
import Carousel from 'react-bootstrap/Carousel'

import { Link } from "react-router-dom";

export default class Profile extends Component {

  constructor(props) {
    super(props);
    
    this.setActiveCharacter = this.setActiveCharacter.bind(this);

    this.state = {
      currentUser: AuthService.getCurrentUser(),
      currentPlayer: "",
      characters: [],
      currentCharacterl: null,
      currentIndex: -1,
    };
    
  }

  setActiveCharacter(character, index) {
    this.setState({
      currentCharacter: character,
      currentIndex: index
    });
  }

  componentDidMount() {

    AuthService.getCurrentPlayer().then(
      response => {
        this.setState({
          currentPlayer: response.data
        }, () =>

        CharacterService.findCharactersByPlayer(this.state.currentPlayer.player_id).then(
          async (data) => {
            console.log(data);
            this.setState({
              characters: data.data
            })
          })
        )  
    }
    )
  }

  render() {
    const { currentUser, currentPlayer, characters, currentCharacter, currentIndex } = this.state;

    return (
      <div className="container">

        
     



        <header className="jumbotron">
          
              <h3>
                <strong>{currentUser.username}</strong>'s Profile
              </h3>
              <p>
              <strong>Player Name: </strong>{" "}
              {currentPlayer.player_name}
              </p>

              <p>
              <strong>Rank: </strong>{" "}
              {currentPlayer.player_rank}
              </p>

              <p>
              <strong>Discord ID: </strong>{" "}
              {currentPlayer.discord_id}
              </p>
        </header>
        
        <div class="push-left">
        <Link to={"/addCharacter"} className="nav-link">
            <button className="btn btn-warning">
            Add Character
            </button>
        </Link>

        </div>

        <div id="car">
          <div data-interval="false" class="carousel slide">
              <ol class="carousel-indicators">
                <li></li>
                <li class="active"></li>
              </ol>
              <div class="carousel-inner">
              {characters.map((character, index) => (
          <Carousel.Item key={index} className={index === 0 ? "active" : ""}>
            <div>
                <label>
                  <strong>Character Name:</strong>
                </label>{" "}
                {character.character_name}
              </div>

              <div>
                <label>
                  <strong>Character Class:</strong>
                </label>{" "}
                {character.character_class}
              </div>

              <div>
                <label>
                  <strong>Level:</strong>
                </label>{" "}
                {character.level}
              </div>

              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {character.status}
              </div>

              <div>
                <label>
                  <strong>Stat Array:</strong>
                </label>{" "}
                {character.stat_array}
              </div>

              <div>
                <label>
                  <strong>Location:</strong>
                </label>{" "}
                {character.location_id}
              </div>

              <Link
                to={"/characters/" + character.character_id}
                className="btn btn-warning"
              >
                Edit
              </Link>

          </Carousel.Item>
           ))}

              </div>
              <a class="carousel-control-prev" role="button" href="#"><span aria-hidden="true" class="carousel-control-prev-icon"></span><span class="sr-only">Previous</span></a><a class="carousel-control-next" role="button" href="#"><span aria-hidden="true" class="carousel-control-next-icon"></span><span class="sr-only">Next</span></a>
          </div>
        </div>

        <div id="car">
        <Carousel data-interval="false" wrap="false" class="carousel-slide">  
          {characters.map((character, index) => (
          <Carousel.Item key={index} className={index === 0 ? "active" : ""}>
            <div>
                <label>
                  <strong>Character Name:</strong>
                </label>{" "}
                {character.character_name}
              </div>

              <div>
                <label>
                  <strong>Character Class:</strong>
                </label>{" "}
                {character.character_class}
              </div>

              <div>
                <label>
                  <strong>Level:</strong>
                </label>{" "}
                {character.level}
              </div>

              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {character.status}
              </div>

              <div>
                <label>
                  <strong>Stat Array:</strong>
                </label>{" "}
                {character.stat_array}
              </div>

              <div>
                <label>
                  <strong>Location:</strong>
                </label>{" "}
                {character.location_id}
              </div>

              <Link
                to={"/characters/" + character.character_id}
                className="btn btn-warning"
              >
                Edit
              </Link>

          </Carousel.Item>
           ))}

        </Carousel>  
        </div>



        
      </div>
    );
  }
}
