import React, { Component } from "react";
import CharacterDataService from "../../services/character.service";
import { Link } from "react-router-dom";

export default class CharactersList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchPlayerName = this.onChangeSearchPlayerName.bind(this);
    this.retrieveCharacters = this.retrieveCharacters.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveCharacter = this.setActiveCharacter.bind(this);
    this.removeAllCharacters = this.removeAllCharacters.bind(this);
    this.searchCharacterName = this.searchCharacterName.bind(this);

    this.state = {
      characters: [],
      currentCharacterl: null,
      currentIndex: -1,
      searchCharacterName: ""
    };
  }

  componentDidMount() {
    this.retrieveCharacters();
  }

  onChangeSearchCharacterName(e) {
    const searchCharacterName = e.target.value;

    this.setState({
      searchCharacterName: searchCharacterName
    });
  }

  retrieveCharacters() {
    CharacterDataService.getAll()
      .then(response => {
        this.setState({
          characters: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveCharacters();
    this.setState({
      currentCharacter: null,
      currentIndex: -1
    });
  }

  setActiveCharacter(character, index) {
    this.setState({
      currentCharacter: character,
      currentIndex: index
    });
  }

  removeAllCharacters() {
    CharacterDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchCharacterName() {
    CharacterDataService.findByCharacterName(this.state.searchCharacterName)
      .then(response => {
        this.setState({
          characters: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchCharacterName, characters, currentCharacter, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by CharacterName"
              value={searchCharacterName}
              onChange={this.onChangeSearchCharacterName}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchCharacterName}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Characters List</h4>

          <ul className="list-group">
            {characters &&
              characters.map((character, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveCharacter(character, index)}
                  key={index}
                >
                  {character.character_name}
                </li>
              ))}
          </ul>
        </div>
        <div className="col-md-6">
          {currentCharacter ? (
            <div>
              <h4>Character</h4>
              <div>
                <label>
                  <strong>Character Name:</strong>
                </label>{" "}
                {currentCharacter.character_name}
              </div>

              <Link
                to={"/characters/" + currentCharacter.character_id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Character...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
